package org.structuredlearning.datastructures.linear;

/**
 * This class is a generic stack
 * @author damiennagle
 *
 * @param <E>
 */
public class Stack<E> {
	
	public Stack() {
		
	}
	
	/**
	 * Returns if the stack is empty
	 * @return true if stack is empty false otherwise
	 */
	public boolean empty() {
		return false;
	}
	
	/**
	 * Returns the element on the top of the stack without removing it
	 * @return Element on the top of the stack
	 */
	public E peek() {
		return null;
	}
	
	/**
	 * Removes the element at the top of the stack
	 * Returns the element removed from the stack
	 * @return element removed from stack
	 */
	public E pop() {
		return null;
	}
	
	/**
	 * Pushes an element on to the top of the stack
	 * returns the element pushed on to the top of the stack
	 * @param item
	 * @return Element pushed onto stack
	 */
	public E push(E item) {
		return null;
	}
	
	/**
	 * Returns the size of the stack
	 * @return Size of stack
	 */
	public int size() {
		return 0;
	}

}
